<script src='https://cdnjs.cloudflare.com/ajax/libs/ekko-lightbox/5.3.0/ekko-lightbox.min.js'></script>
<link href='https://cdnjs.cloudflare.com/ajax/libs/ekko-lightbox/5.3.0/ekko-lightbox.css' rel='stylesheet'>
<script src="/public/themes/frontend/yenbinh/themes/chuyennt/js/owl.carousel.js"></script>
<link rel='stylesheet' href="/public/themes/frontend/yenbinh/themes/chuyennt/css/owl.carousel.css">
<link rel='stylesheet' href="/public/themes/frontend/yenbinh/themes/chuyennt/css/owl.theme.css">
<style>
	#mygallery__2078 .item{padding:5px; overflow:hidden; min-width:300px;}
	#mygallery__2078 .item.slider{padding:0px; text-align:center;}
	@media (max-width: 768px) {  
	    #mygallery__2078{ max-width: 510px; min-width: 470px; }
	}
	@media (max-width: 576px) {  
	    #mygallery__2078{ max-width: 470px; min-width: 470px; }
	}
	@media (max-width: 450px) {  
	    #mygallery__2078{ max-width:380px; min-width: 350px;}
	}
	@media (max-width: 380px) {  
	    #mygallery__2078{ max-width:345px; min-width: 320px;}
	}
	#mygallery__2078 img{ width: 100%!important; height:500px!important;}
	.mygallery__prev {
	    position: absolute;
	    top: 48%;
	    left: 20px;
	    display: inline-block;
	    width: 17px;
	    height: 30px;
	    background: url(data:image/png;base64,) center center no-repeat;
	    cursor: pointer;
	}
	.mygallery__next {
	    position: absolute;
	    top: 48%;
	    right: 20px;
	    display: inline-block;
	    width: 17px;
	    height: 30px;
	    background: url(data:image/png;base64,) center center no-repeat;
	    cursor: pointer;
	}
</style>
<script>
$(document).ready(function ($) {
	var mygallery = $('#mygallery__2078');
	if (mygallery.length > 0) {
        mygallery.owlCarousel({
       		items: 1, //10 items above 1000px browser width
            itemsDesktop: [1000, 2], //5 items between 1000px and 901px
            itemsDesktopSmall: [900, 1], // 3 items betweem 900px and 601px
            itemsTablet: [600, 1], //2 items between 600 and 0;
            itemsMobile: [400, 1], // itemsMobile disabled - inherit from itemsTablet option
            pagination:false,
        });
    }
    // disable wrapping
	$(document).on('click', '[data-toggle="lightbox"][data-gallery="gallery-2078"]', function(event) {
	event.preventDefault();
	return $(this).ekkoLightbox({
	    wrapping: false
	});
	});
});
function next_OWL1679762638(obj) {
    $('#' + obj).trigger('owl.next');
}

function prev_OWL1679762638(obj) {
    $('#' + obj).trigger('owl.prev');
}
</script>